<?phpnamespace sts;use sts as core;if (!defined(__NAMESPACE__ . '\ROOT')) exit;if ($config->get('api_enabled')) {	if ($config->get('api_require_https')) {		if (!isset($_SERVER['HTTPS']) || ($_SERVER['HTTPS'] != 'on')) {			exit;		}	}	header('Cache-Control: no-cache, must-revalidate');	header('Content-Type: application/json; charset=utf-8');	$return = json_encode(array());	if (isset($_POST['data'])) {		$return = $api->receive(array('data' => $_POST['data']));	}	echo $return;}?>